import {
    VoiceChannel,
    CommandInteraction,
    Interaction
} from 'discord.js';

interface playOptions {
    interaction: Interaction | CommandInteraction;
    channel: VoiceChannel;
    url: string;
};

interface basicOptions {
    interaction: Interaction | CommandInteraction
};

interface repeatOptions {
    interaction: Interaction | CommandInteraction;
    value: boolean;
};

interface numberOptions {
    interaction: Interaction | CommandInteraction;
    number: number;
};

interface volumeOptions {
    interaction: Interaction | CommandInteraction;
    volume: number;
}

declare module '@koenie06/discord.js-music' {
    declare function play(params: playOptions): Promise<void>;
    declare function stop(params: basicOptions): Promise<void>;
    declare function isConnected(params: basicOptions): Promise<boolean>;
    declare function isRepeated(params: basicOptions): Promise<boolean>;
    declare function isPaused(params: basicOptions): Promise<boolean>;
    declare function isResumed(params: basicOptions): Promise<boolean>;
    declare function repeat(params: repeatOptions): Promise<void>;
    declare function skip(params: basicOptions): Promise<void>;
    declare function pause(params: basicOptions): Promise<void>;
    declare function resume(params: basicOptions): Promise<void>;
    declare function jump(params: numberOptions): Promise<void>;
    declare function getQueue(params: basicOptions): Promise<void>;
    declare function volume(params: volumeOptions): Promise<void>;
    declare function removeQueue(params: numberOptions): Promise<void>;
};